css:
.print-format {
    padding: 0 !important;
    position: relative;
}
h1{
    margin: 0 !important;
}
.print-format tfoot{
    display: table-footer-group !important;

}
#rua-header{
    height: 30mm; 
    width: 211mm;
    border-radius: 0 0 var(--border-radius-lg) var(--border-radius-lg);
}
#rua-header-left{
    width: 110px;
    height: 110px;
    background-color: #f0f0f0;
    border-radius: 0 var(--border-radius-lg) 0 var(--border-radius-lg);
    display: flex;
    justify-content: center;
    align-items: center;
}
#rua-header-right{
    display: flex;
    flex-grow: 1;
    background-color: #fff;
    border-radius: var(--border-radius-lg);
}
#rua-header-overlay{
    position: absolute;
    width: 662px;
    height: 121px;
    background-color: white;
    border-radius: var(--border-radius-lg) var(--border-radius-lg) 0 0;
    right: -10px;
    top: 0px;
    display: flex;
    justify-content: space-between;
    padding: 20px;
    align-items: center;
}
#rua-header-wrapper{
    display: flex; gap:15px; max-height: 30mm; position:relative;
}

#footer-spacer{
    height: 25mm;
}
#rua-footer{
    width: 211mm;
    height: 25mm;
    position: absolute;
    bottom: 0;
    background-color: #17171710;
}
#rua-footer-wrapper{
    display: flex;
    max-height: 25mm; position: relative;
    gap: 15px;
    position: relative;
}
#rua-footer-logo{
    display: flex;
    align-items: center;
    justify-content: center;
}
#rua-footer-logo img{
    width: 68px;
    margin-left: 10px;
}
#rua-footer-company{
    padding: 15px 0;
    display: flex;
    flex-direction: column;
}
#rua-footer-company .company-name{
    font-weight: 800;
}
#rua-footer-company span{
    display: flex;
    align-items: center;
}

.company-contact span{
    display: flex;
    align-items: center;
    gap: 2px;
}
.company-contact span a{
    display: flex;
    align-items: center;
}
.company-contact span a:hover{
    text-decoration: none;
}

.company-contact{
    display: flex;
    align-items: center;
    gap: 10px;
}
@media print {
  @page {
    @bottom-right {
      content: "Page " counter(page) " of " counter(pages) "\A\A\A\A\A";
      margin-right: 10px;
      white-space: pre;
      z-index: 999;
      font-weight: bold;
      font-size: 9px;
    }
  }
  #rua-footer {
    position: fixed;
  }
}
.material-symbols-outlined{
    font-size: 17px !important;
    color: inherit !important;
    font-weight: 600 !important;
}
#signature-area{
    display: flex;
    flex-direction: column;
    justify-content: end;
    flex-grow: 1;
    padding: 0 10px 2px 10px;
    color: #b0b2b3;
}
#signature-area small{
    color: #b0b2b3;
}
#signature-area hr{
    margin: 0 !important;
    border-top: 1px dashed #b0b2b3;
}
#rua-footer-signature{
    display: flex;
    flex-grow: 1;
}
#page-signature{
    position: absolute;
    max-width: 200px;
    max-height: 120px;
    bottom: 3px;
    object-fit: contain;
    right: 25px;
}
#rua-doc-title{
    display: flex;
    flex-direction: column;
    flex-grow: 1;
}
#rua-doc-title .doc-type{
    font-weight: 700;
    font-size: 24px;
}
#rua-doc-title .doc-name{
    font-size: 18px;
    font-weight: 800;
    padding: 4px 8px;
    width: fit-content;
    border-radius: var(--border-radius-lg);
}
#rua-doc-title .doc-date{
    font-size: 16px;
    font-weight: 600;
}
